package Analyzer;import java.util.Arrays;import java.util.Map;import java.util.stream.Collectors;public interface Analyzer<T> {    static Map<String,Long> getWordsMap(String text) {        return Arrays.stream(text                                .toLowerCase()                                .split("[/. -]|\\r?\\n"))                .map(x->x.replaceAll("[^\\p{LD}\\s]",""))                .filter(x->!x.matches(""))                .collect(Collectors.groupingBy(x->x, Collectors.counting()));    }    T analyze(String text);    String analyzeWithFormattedOutput(String text);}