package Analyzer;import java.util.Arrays;import java.util.List;import java.util.Map;import java.util.stream.Collectors;public class LongestSingleWordsAnalyzer implements Analyzer<List<String>> {    @Override    public List<String> analyze(String text) {        Map<String,Long> wordsMap = Arrays.stream(text.split(" "))                .collect(Collectors.groupingBy(x->x, Collectors.counting()));        return wordsMap                .entrySet()                .stream()                .filter(x->x.getValue() == 1)                .sorted((x,y)->y.getKey().length() - x.getKey().length())                .limit(10)                .map(Map.Entry::getKey)                .collect(Collectors.toList());    }    @Override    public String analyzeWithFormattedOutput(String text) {        return "The longest single words are: " + analyze(text).toString();    }}