package Analyzer;import java.util.Map;public class NumberOfWordsAnalyzer implements Analyzer<Long> {    @Override    public Long analyze(String text) {        Map<String,Long> wordsMap = Analyzer.getWordsMap(text);        return wordsMap                .values()                .stream()                .reduce(Long::sum)                .orElseThrow(IllegalArgumentException::new);    }    @Override    public String analyzeWithFormattedOutput(String text) {        return "Number of words in text is: " + analyze(text).toString();    }}