package Analyzer;import java.util.Arrays;import java.util.Map;import java.util.stream.Collectors;public class NumberOfWordsAnalyzer implements Analyzer<Long> {    @Override    public Long analyze(String text) {        Map<String,Long> wordsMap = Arrays.stream(text.split(" "))                .collect(Collectors.groupingBy(x->x, Collectors.counting()));        return wordsMap                .values()                .stream()                .reduce(Long::sum)                .get();    }    @Override    public String analyzeWithFormattedOutput(String text) {        return "Number of words in text is: " + analyze(text).toString();    }}