import Analyzer.*;import LanguageDetector.LocalLanguageDetector;import java.util.Arrays;import java.util.List;public class MultiAnalizer {    public static void callAnalyzers(String text){        List<Analyzer> analyzers = Arrays.asList(new CharFrequencyAnalyzer(),                                                 new LongestSingleWordsAnalyzer(),                                                 new MostFrequentWordsAnalyzer(),                                                 new NumberOfWordsAnalyzer());        for(Analyzer analyzer : analyzers){            System.out.println(analyzer.analyzeWithFormattedOutput(text));        }        System.out.println(new LocalLanguageDetector().detect(new CharFrequencyAnalyzer().analyze(text)));    }}